import React, { Component } from 'react';
import { connect } from 'react-redux';
import { login } from '../actions/LoginActions';

const mapDispatchToProps = dispatch => {
    return{
        login: (username, password)=>{
            dispatch(login(username, password));
        }
    }
}

const mapStateToProps = state => ({
    locations: state.locations.items,
    loading: state.locations.loading,
    error: state.locations.error
  });

class Login extends Component {
    constructor(props){
        super(props);

        this.state = {
            usernameInput: '',
            passwordInput: ''
        }
    }

    render(){
        const { error, loading } = this.props;
        return (
            <div className="Login">
                <input type='text' value={this.state.usernameInput} placeholder="Username" onChange={evt => this.updateUsernameInput}/>
                <input type='password' value={this.state.passwordInput} placeholder="password" onChange={evt => this.updatePasswordInput}/>
                <button onClick="">
                    Login
                </button>
            </div>
        )
    }

    updateUsernameInput(evt){
        this.setState({
            usernameInput: evt.target.value
        });
    }

    updatePasswordInput(evt){
        this.setState({
            passwordInput: evt.target.value
        });
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Login)
