import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class AddEventButton extends Component {
    render(){
        return(
            <div>
                <addEventButtonLink to="/addEvent" label_text="Add an Event !" label_button="+" />
            </div>
        )
    }
}


export class addEventButtonLink extends Component{
    render(){
        return (
            <Route
                path={this.props.to}
                exact={this.props.activeOnlyWhenExact}
                children={({ match }) => (
                <div className={match ? "addEvent active" : "addEvent"} >
                    <Link to={this.props.to}>
                        <p className="addEvent_text">{this.props.label_text}</p>
                        <p className="addEvent_button">{this.props.label_button}</p>
                </Link>
                </div>
                )}
            />
        )
    }
}

export default AddEventButton
